import React, {useState} from 'react';
import './App.css';
import ToDoTable from './components/ToDoTable';
import NewTodoForm from './components/NewTodoForm';

function App() {

  const[showAddTodoForm, setShowAddTodoForm] = useState(false);

  const[todos, setTodos] = useState([
    {rowNumber:1, rowDescription:'Feed Dog', rowAssigned:'User 1'},
    {rowNumber:2, rowDescription:'Feed Cat', rowAssigned:'User 2'},
    {rowNumber:3, rowDescription:'Make Dinner', rowAssigned:'User 1'},
    {rowNumber:4, rowDescription:'Eat Dinner', rowAssigned:'User 1'},
  ])

  const addTodo = (description, assigned) => {
    let rowNumber = 0;
    if(todos.length > 0) {
      rowNumber = todos[todos.length-1].rowNumber + 1;
    } else {
      rowNumber = 1;
    }
      const newTodo = {
        rowNumber: rowNumber,
        rowDescription: description,
        rowAssigned: assigned
      };
      setTodos(todos => [...todos, newTodo]);
    }

    const deleteTodo = (deleteTodoRowNumber) => {
      let filtered = todos.filter(function (value) {
        return value.rowNumber !== deleteTodoRowNumber;
      });
      setTodos(filtered);
    }

  return (
    <div className='mt-5 container'>
      <div className='card'>
        <div className='card-header'>
          Your ToDo's
        </div>
        <div className='card-body'>
          <ToDoTable todos={todos} deleteTodo={deleteTodo}/>
          {<button className='btn btn-primary' onClick={ () => showAddTodoForm(!showAddTodoForm)}>
            New ToDo
          </button>}
          <NewTodoForm addTodo={addTodo}/>
        </div>
      </div>
    </div>
  );
}

export default App;
